mapper.select:
select

from user


		and user_id = #{userId,jdbcType=INTEGER}


		and name like
		CONCAT('%',#{name,jdbcType=VARCHAR},'%')


		and department = #{department,jdbcType=VARCHAR}


		and user_name like
		CONCAT('%',#{userName,jdbcType=VARCHAR},'%')


		and is_valid = #{isValid,jdbcType=BIT}
