mapper.update:
update workflow_node


		is_valid = #{isValid,jdbcType=BIT},


		modified_by = #{modifiedBy,jdbcType=VARCHAR},




		and workflow_node_id = #{workflowNodeId,jdbcType=INTEGER}


		and workflow_id = #{workflowId,jdbcType=INTEGER}

mapper.select:
select

from workflow_node


		and workflow_node_id = #{workflowNodeId,jdbcType=INTEGER}


		and workflow_id = #{workflowId,jdbcType=INTEGER}


		and is_valid = #{isValid,jdbcType=BIT}


order by sort, create_time

select a.workflow_node_id, a.workflow_id, a.workflow_node_name, a.sort, a.remark, a.approver, b.name as approverName,a.is_valid, a.create_by, cast(a.create_time as char) as create_time, a.modified_by, cast(a.modified_time as char) as modified_time from workflow_node a left join user b on a.approver = b.user_name
and a.workflow_node_id = #{workflowNodeId,jdbcType=INTEGER}
and a.workflow_id = #{workflowId,jdbcType=INTEGER}
and a.is_valid = #{isValid,jdbcType=BIT}
order by sort, create_time
